import { Enser } from "./Enser";

/**
 * Clase que representa una caja para almacenar enseres durante una mudanza.
 * Permite agregar, eliminar, buscar y listar enseres, además de calcular el peso total.
 */
export class Caja<T extends Enser> {
    /** Array para los enseres almacenados en la caja. */
    private contenidos: T[] = [];

    /**
     * Crea una instancia de una caja.
     * @param id Identificador único para la caja.
     */
    constructor(public id: number) {}

    /**
     * Agrega un enser a la caja.
     * @param enser El enser a agregar.
     */
    public agregarEnser(enser: T): void {
        this.contenidos.push(enser);
    }

    /**
     * Elimina un enser de la caja basado en su nombre.
     * @param nombre El nombre del enser a eliminar.
     */
    public eliminarEnser(nombre: string): void {
        this.contenidos = this.contenidos.filter(enser => enser.nombre !== nombre);
    }

    /**
     * Obtiene la cantidad de enseres contenidos en la caja.
     * @returns El número de enseres en la caja.
     */
    public cantidadEnseres(): number {
        return this.contenidos.length;
    }

    /**
     * Lista todos los enseres contenidos en la caja.
     * @returns Una cadena con la lista de enseres.
     */
    public listarEnseres(): string {
        if (this.contenidos.length === 0) {
            return "La caja está vacía.";
        }

        let resultado = "Contenidos de la caja:\n";
        this.contenidos.forEach(enser => {
            resultado += `${enser.nombre}: ${enser.pesokg}kg, ${enser.fragil ? "frágil" : "no frágil"}\n`;
        });
        return resultado;
    }

    /**
     * Busca un enser en la caja basado en su nombre.
     * @param nombre El nombre del enser a buscar.
     * @returns El enser encontrado, o `undefined` si no se encuentra.
     */
    public buscarEnser(nombre: string): string {
        const enser = this.contenidos.find(enser => enser.nombre === nombre);
        if (enser) {
            return `Enser encontrado: ${enser.nombre}`;
        } else {
            return 'Enser no encontrado';
        }
    }    

    /**
     * Calcula el peso total de la caja sumando el peso de todos los enseres.
     * @returns El peso total de la caja en kilogramos.
     */
    public pesoTotal(): number {
        return this.contenidos.reduce((total, enser) => total + enser.pesokg, 0);
    }
}

import { Enser } from "./Enser";
import { Caja } from "./Caja";

/**
 * Clase que representa la gestión de una mudanza, permitiendo manejar múltiples cajas de enseres.
 */
export class Mudanza<T extends Enser>{
    /** Colección de cajas utilizadas en la mudanza. */
    private cajas: Caja<T>[] = [];

    /**
     * Crea una instancia de gestión de mudanza.
     */
    constructor() {
        this.cajas = [];
    }

    /**
     * Agrega una caja a la colección de cajas de la mudanza.
     * @param caja La caja a agregar.
     */
    public agregarCaja(caja: Caja<T>): void {
        this.cajas.push(caja);
    }

    /**
     * Elimina una caja de la colección basado en su índice.
     * @param indice El índice de la caja a eliminar.
     */
    public eliminarCaja(indice: number): void {
        if (indice < 0 || indice >= this.cajas.length) {
            throw new Error("Índice de caja fuera de rango.");
        }
        this.cajas.splice(indice, 1);
    }

    /**
     * Obtiene la cantidad de cajas en la mudanza.
     * @returns El número de cajas en la mudanza.
     */
    public cantidadCajas(): number {
        return this.cajas.length;
    }

    /**
     * Lista todas las cajas de la mudanza, incluyendo sus enseres.
     * @returns Una cadena con la lista de cajas y sus enseres.
     */
    public listarCajas(): string {
        if (this.cajas.length === 0) {
            return "No hay cajas.";
        }

        let resultado = "";
        this.cajas.forEach((caja, i) => {
            // Asumiendo que caja.listarEnseres() ahora devuelve un string.
            resultado += `[Caja ${i + 1}]\n${caja.listarEnseres()}\n`;
        });

        return resultado.trim(); // Elimina espacios en blanco extra al final
    }

    /**
     * Calcula el peso total de la mudanza sumando el peso de todas las cajas.
     * @returns El peso total de la mudanza.
     */
    public pesoTotalMudanza(): number {
        return this.cajas.reduce((total, caja) => total + caja.pesoTotal(), 0);
    }
}

import { Factura } from './IFactura';

/**
 * Implementación concreta de la interfaz Factura.
 */
export class FacturaSimple implements Factura {
    /**
     * Construye una instancia de FacturaSimple.
     * @param id Identificador único de la factura.
     * @param emisor Nombre o identificación del emisor de la factura.
     * @param receptor Nombre o identificación del receptor de la factura.
     * @param cantidad Cantidad total de la factura.
     * @param fechaVencimiento Fecha de vencimiento de la factura.
     * @param estaPagada Indica si la factura está pagada o no.
     */
    constructor(public id: string, public emisor: string, public receptor: string, public cantidad: number,
                public fechaVencimiento: Date, public estaPagada: boolean = false) {}

    /**
     * Comprueba si la factura está pagada.
     * @returns true si la factura está pagada, false en caso contrario.
     */

    public getPagada(): boolean {
        return this.estaPagada;
    }
    /**
     * Marca la factura como pagada.
     */
    public marcarPagada() {
        this.estaPagada = true;
    }
}

import { Factura } from './IFactura';
import { ExportadorFactura } from './exportadorFactura';
import { IGestorFacturas } from './IGestorFacturas';

/**
 * Gestiona el conjunto de facturas en el sistema.
 */
export class GestorFacturas implements IGestorFacturas{
    /**
     * Almacena las facturas en un array.
     */
    private facturas: Factura[];

    /**
     * Constructor que inicializa la lista de facturas.
     */
    constructor() {
      this.facturas = [];
    }

    /**
     * Agrega una nueva factura al sistema.
     * @param factura La instancia de `Factura` a ser agregada.
     */
    public agregarFactura(factura: Factura) {
      this.facturas.push(factura);
    }

    /**
     * Obtiene una factura por su identificador único (ID).
     * @param id El identificador de la factura a buscar.
     * @returns La instancia de `Factura` encontrada o `undefined` si no existe una factura con el ID proporcionado.
     */
    public obtenerFactura(id: string): Factura | undefined {
      return this.facturas.find(factura => factura.id === id);
    }

    /**
     * Exporta una factura específica utilizando un exportador dado.
     * @param id El identificador de la factura a exportar.
     * @param exportador Una instancia que implementa la interfaz `ExportadorFactura`, utilizada para realizar la exportación.
     * @returns La representación de la factura en el formato especificado por el exportador, o `undefined` si la factura no se encuentra.
     */
    public exportarFactura(id: string, exportador: ExportadorFactura): string | undefined {
      const factura = this.obtenerFactura(id);
      if (!factura) {
        console.log("Factura no encontrada.");
        return;
      }
      return exportador.exportar(factura);
    }
}

import { Factura } from './IFactura';
import { ExportadorFactura } from './exportadorFactura';

/**
 * Exportador de facturas en formato HTML.
 */
export class ExportadorHTML implements ExportadorFactura {  
    /**
     * Exporta una factura a formato HTML.
     * @param factura La factura a exportar.
     * @returns La factura en formato HTML.
     */
    public exportar(factura: Factura): string {
      return `<h1>Factura ${factura.id}</h1>\n<p>Emisor: ${factura.emisor}</p>\n<p>Receptor: ${factura.receptor}</p>\n<p>Cantidad: ${factura.cantidad}</p>\n<p>Fecha de Vencimiento: ${factura.fechaVencimiento.toLocaleDateString()}</p>\n<p>Estado: ${factura.estaPagada ? 'Pagada' : 'Pendiente de pago'}</p>`;
    }
}

import { Factura } from './IFactura';
import { ExportadorFactura } from './exportadorFactura';

/**
 * Exportador de facturas en formato PDF.
 */
export class ExportadorPDF implements ExportadorFactura {
    /**
     * Exporta una factura a formato PDF.
     * @param factura La factura a exportar.
     * @returns La representación de la factura como si fuera un PDF.
     */
    public exportar(factura: Factura): string {
      return `Factura ${factura.id}\nEmisor: ${factura.emisor}\nReceptor: ${factura.receptor}\nCantidad: ${factura.cantidad}\nFecha de Vencimiento: ${factura.fechaVencimiento.toLocaleDateString()}\nEstado: ${factura.estaPagada ? 'Pagada' : 'Pendiente de pago'}`;
    }
}

import * as fs from 'fs';

import { IFileReader } from './IFileReader';

/**
 * Implementación concreta de IFileReader para leer archivos del sistema de archivos.
 */
export class FileReader implements IFileReader {
    /**
     * Inicializa una nueva instancia de la clase FileReader.
     * @param filePath La ruta del archivo a leer.
     */
    constructor(private filePath: string) {}

    /**
     * Lee el contenido del archivo especificado por filePath.
     * @returns El contenido del archivo como una cadena de texto.
     */
    readFile(): string {
      try {
        return fs.readFileSync(this.filePath, 'utf-8');
      } catch (error) {
        console.error('Error al leer el archivo:', error.message);
        return '';
      }
    }
}

import * as fs from 'fs';

import { IFileWriter } from './IFileWriter';

/**
 * Implementación concreta de IFileWriter para escribir archivos en el sistema de archivos.
 */
export class FileWriter implements IFileWriter {
    /**
     * Inicializa una nueva instancia de la clase FileWriter.
     * @param filePath La ruta del archivo en el que escribir.
     */
    constructor(private filePath: string) {}

    /**
     * Escribe los datos proporcionados en el archivo especificado por filePath.
     * @param data Los datos a escribir en el archivo.
     */
    writeFile(data: string): void {
      try {
        fs.writeFileSync(this.filePath, data, 'utf-8');
        console.log('Archivo escrito exitosamente.');
      } catch (error) {
        console.error('Error al escribir en el archivo:', error.message);
      }
    }
}

import { IFileReader } from "./IFileReader";
import { IFileWriter } from "./IFileWriter";

/**
 * Clase fachada para gestionar operaciones de archivos usando FileReader y FileWriter.
 */
export class FileManager {
    /**
     * Inicializa una nueva instancia de la clase FileManager.
     * @param reader Una instancia de IFileReader para leer archivos.
     * @param writer Una instancia de IFileWriter para escribir archivos.
     */
    constructor(private reader: IFileReader, private writer: IFileWriter) {}

    /**
     * Lee el contenido del archivo.
     * @returns El contenido del archivo como una cadena de texto.
     */
    read(): string {
      return this.reader.readFile();
    }

    /**
     * Escribe los datos proporcionados en el archivo.
     * @param data Los datos a escribir en el archivo.
     */
    write(data: string): void {
      this.writer.writeFile(data);
    }
  }

